mapper.delete:
delete from neuedu_cart
where id  in

  #{cart.id}

mapper.select:

select id, user_id, product_id, quantity, checked, create_time, update_time
from neuedu_cart
where id = #{id,jdbcType=INTEGER}

select id, user_id, product_id, quantity, checked, create_time, update_time
from neuedu_cart
where user_id = #{userId}

select id, user_id, product_id, quantity, checked, create_time, update_time
from neuedu_cart
where user_id = #{userId} and product_id=#{productId}
